CREATE PROCEDURE spIns_Cg_marca	@ID_MARCA As int,	@DESC_MARCA As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_MARCA (	id_marca,	desc_marca,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_marca,	@desc_marca,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------