CREATE PROCEDURE spUpd_Cg_marca	@ID_MARCA As int,	@DESC_MARCA As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_MARCA SET	desc_marca = @desc_marca,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_MARCA = @ID_MARCAEND------------------------------------------------------------