CREATE PROCEDURE spIns_Cg_acesso	@ID_USUARIO As int,	@ID_MODULO As int,	@PERMISSAO As char(1) = NULL,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULL,	@ID_EMPRESA As intASBEGININSERT INTO CG_ACESSO (	id_usuario,	id_modulo,	permissao,	user_ins,	data_ins,	user_upd,	data_upd,	id_empresa)VALUES (	@id_usuario,	@id_modulo,	@permissao,	@user_ins,	@data_ins,	@user_upd,	@data_upd,	@id_empresa)END------------------------------------------------------------