CREATE PROCEDURE spUpd_Cg_finalidade	@ID_FINALIDADE As int,	@DESC_FINALIDADE As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULL,	@ID_EMPRESA As intASBEGINUPDATE CG_FINALIDADE SET	desc_finalidade = @desc_finalidade,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_upd,	id_empresa = @id_empresaWHERE	ID_FINALIDADE = @ID_FINALIDADEEND------------------------------------------------------------