CREATE PROCEDURE spIns_Cg_mov_transito_equipamento	@ID_LANCTO As bigint,	@ID_TRANSITO As int,	@ID_ORIGEM As int,	@ID_EQUIPAMENTO As int,	@QUANTIDADE As int,	@USER_INS As int,	@DATA_LANCTO As datetime,	@ID_DESTINO As int = NULL,	@DATA_MOV_DESTINO As datetime = NULL,	@ID_EMPRESA As intASBEGININSERT INTO CG_MOV_TRANSITO_EQUIPAMENTO (	id_lancto,	id_transito,	id_origem,	id_equipamento,	quantidade,	user_ins,	data_lancto,	id_destino,	data_mov_destino,	id_empresa)VALUES (	@id_lancto,	@id_transito,	@id_origem,	@id_equipamento,	@quantidade,	@user_ins,	@data_lancto,	@id_destino,	@data_mov_destino,	@id_empresa)END------------------------------------------------------------