CREATE PROCEDURE spIns_Cg_operadora	@ID_OPERADORA As int,	@DESC_OPERADORA As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULL,	@ID_EMPRESA As intASBEGININSERT INTO CG_OPERADORA (	id_operadora,	desc_operadora,	user_ins,	data_ins,	user_upd,	data_upd,	id_empresa)VALUES (	@id_operadora,	@desc_operadora,	@user_ins,	@data_ins,	@user_upd,	@data_upd,	@id_empresa)END------------------------------------------------------------