if exists(select 1 from sysobjects where TYPE = 'P' AND name = 'spDel_Cg_tipo_equipamento')begin	drop procedure spDel_Cg_tipo_equipamentoendGOCREATE PROCEDURE spDel_Cg_tipo_equipamento	@ID_TIPO_EQUIPAMENTO As intASBEGINDELETE FROM CG_TIPO_EQUIPAMENTO WHERE	ID_TIPO_EQUIPAMENTO = @ID_TIPO_EQUIPAMENTOENDGO------------------------------------------------------------