if exists(select 1 from sysobjects where TYPE = 'P' AND name = 'spIns_Cg_tipo_servico')begin	drop procedure spIns_Cg_tipo_servicoendGOCREATE PROCEDURE spIns_Cg_tipo_servico	@ID_TIPO_SERVICO As int,	@DESC_TIPO_SERVICO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULL,	@ID_EMPRESA As intASBEGININSERT INTO CG_TIPO_SERVICO (	id_tipo_servico,	desc_tipo_servico,	user_ins,	data_ins,	user_upd,	data_upd,	id_empresa)VALUES (	@id_tipo_servico,	@desc_tipo_servico,	@user_ins,	@data_ins,	@user_upd,	@data_upd,	@id_empresa)ENDGO------------------------------------------------------------