if exists(select 1 from sysobjects where TYPE = 'P' AND name = 'spIns_Cg_transito')begin	drop procedure spIns_Cg_transitoendGOCREATE PROCEDURE spIns_Cg_transito	@ID_TRANSITO As int,	@NOME_TRANSITO As varchar(40),	@INATIVO As bit,	@ID_EMPRESA As intASBEGININSERT INTO CG_TRANSITO (	id_transito,	nome_transito,	inativo,	id_empresa)VALUES (	@id_transito,	@nome_transito,	@inativo,	@id_empresa)ENDGO------------------------------------------------------------