if exists(select 1 from sysobjects where TYPE = 'P' AND name = 'spUpd_Romaneio_estoque')begin	drop procedure spUpd_Romaneio_estoqueendGOCREATE PROCEDURE spUpd_Romaneio_estoque	@ROMANEIO As int,	@MODULO As varchar(50) = NULL,	@TIPO_PRODUTO As char(1),	@TIPO_OP As char(1) = NULL,	@DATAMOV As datetime,	@ID_USUARIO As int,	@ID_EMPRESA As intASBEGINUPDATE ROMANEIO_ESTOQUE SET	modulo = @modulo,	tipo_produto = @tipo_produto,	tipo_op = @tipo_op,	datamov = @datamov,	id_usuario = @id_usuario,	id_empresa = @id_empresaWHERE	ROMANEIO = @ROMANEIOENDGO------------------------------------------------------------