if exists(select 1 from sysobjects where TYPE = 'P' AND name = 'spDel_Romaneio_estoque_item')begin	drop procedure spDel_Romaneio_estoque_itemendGOCREATE PROCEDURE spDel_Romaneio_estoque_item	@ROMANEIO As int,	@UNIQUE_KEY As uniqueidentifierASBEGINDELETE FROM ROMANEIO_ESTOQUE_ITEM WHERE	ROMANEIO = @ROMANEIO AND	UNIQUE_KEY = @UNIQUE_KEYENDGO------------------------------------------------------------