CREATE PROCEDURE spUpd_Cg_pedido_compra_peca	@ID_PEDIDO As int,	@TIPO_PEDIDO As char(2),	@STATUS_PEDIDO As varchar(10),	@ID_FORNECEDOR As int,	@ID_COMPRADOR As int,	@DATA_COMPRA As datetime,	@PREVISAO_ENTREGA As datetime,	@DATA_RECEBIMENTO As datetime = NULL,	@OBS_PEDIDO As text = NULL,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULL,	@QTD_TOTAL As int = NULLASBEGINUPDATE CG_PEDIDO_COMPRA_PECA SET	tipo_pedido = @tipo_pedido,	status_pedido = @status_pedido,	id_fornecedor = @id_fornecedor,	id_comprador = @id_comprador,	data_compra = @data_compra,	previsao_entrega = @previsao_entrega,	data_recebimento = @data_recebimento,	obs_pedido = @obs_pedido,	user_upd = @user_upd,	data_upd = @data_upd,	qtd_total = @qtd_totalWHERE	ID_PEDIDO = @ID_PEDIDOEND------------------------------------------------------------