CREATE PROCEDURE spUpd_Cg_pedido_compra_peca_item	@ID_PEDIDO As int,	@ID_PECA As int,	@QTDE As int,	@VALOR_UNIT As numeric(12,2),	@VALOR_TOTAL As numeric(12,2),	@RECEBIDO As bit = 0,	@DATA_RECEBIMENTO As datetime = NULLASBEGINUPDATE CG_PEDIDO_COMPRA_PECA_ITEM SET	qtde = @qtde,	valor_unit = @valor_unit,	valor_total = @valor_total,	recebido = @recebido,	data_recebimento = @data_recebimentoWHERE	ID_PEDIDO = @ID_PEDIDO AND	ID_PECA = @ID_PECAEND------------------------------------------------------------