CREATE PROCEDURE spUpd_Cg_chip	@ID_CHIP As int,	@SIMID As varchar(20),	@ID_OPERADORA As int,	@ID_FORNECEDOR As int,	@CUSTO As numeric(14,2) = NULL,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULL,	@ID_EMPRESA As intASBEGINUPDATE CG_CHIP SET	simid = @simid,	id_operadora = @id_operadora,	id_fornecedor = @id_fornecedor,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_upd,	custo = @custo,	id_empresa = @id_empresaWHERE	ID_CHIP = @ID_CHIPEND------------------------------------------------------------