if exists(select 1 from sysobjects where TYPE = 'P' AND name = 'spIns_Cg_os_item')begin	drop procedure spIns_Cg_os_itemendGOCREATE PROCEDURE spIns_Cg_os_item	@ID_OS As int,	@ITEM_ID As int,	@ID_EQUIPAMENTO As int,	@DESC_EQUIPAMENTO As varchar(50),	@SERIE As varchar(30) = NULL,	@MODELO As varchar(30) = NULL,	@PROTOCOLO As varchar(30) = NULL,	@PREVISAO_RETORNO As datetime = NULL,	@DATA_RETORNO As datetime = NULL,	@DESC_DEFEITO As varchar(250) = NULL,	@CONSERTO_OK As bit,	@NF_FORNEC As varchar(10) = NULL,	@SERIE_NF_FORNEC As varchar(10) = NULL,	@EMISSAO_NF_FORNEC As datetime = NULL,	@OBS_RETORNO As text = NULL,	@ID_RESPONSAVEL_RET As int = NULL,	@XML_NF_FORNEC As xml = NULL,	@LAUDO_FORNEC As image = NULL,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULL,	@GARANTIA As bit,	@RECEBIDO As bit,	@VALOR_CONSERTO As numeric(12,2) = NULL,	@PDF_NF_FORNEC As image = NULL,	@MUDOU_SERIE As bit,	@CONFIG_GARANTIA As bit,	@QTDE_DIAS_GARANTIA As int = NULL,	@DATA_INICIO_GARANTIA As datetime = NULL,	@DATA_TERMINO_GARANTIA As datetime = NULL,	@ID_OCORRENCIA As int = NULL,	@ID_EMPRESA As intASBEGININSERT INTO CG_OS_ITEM (	id_os,	item_id,	id_equipamento,	desc_equipamento,	serie,	modelo,	protocolo,	previsao_retorno,	data_retorno,	desc_defeito,	conserto_ok,	nf_fornec,	serie_nf_fornec,	emissao_nf_fornec,	obs_retorno,	id_responsavel_ret,	xml_nf_fornec,	laudo_fornec,	user_ins,	data_ins,	user_upd,	data_upd,	garantia,	recebido,	valor_conserto,	pdf_nf_fornec,	mudou_serie,	config_garantia,	qtde_dias_garantia,	data_inicio_garantia,	data_termino_garantia,	id_ocorrencia,	id_empresa)VALUES (	@id_os,	@item_id,	@id_equipamento,	@desc_equipamento,	@serie,	@modelo,	@protocolo,	@previsao_retorno,	@data_retorno,	@desc_defeito,	@conserto_ok,	@nf_fornec,	@serie_nf_fornec,	@emissao_nf_fornec,	@obs_retorno,	@id_responsavel_ret,	@xml_nf_fornec,	@laudo_fornec,	@user_ins,	@data_ins,	@user_upd,	@data_upd,	@garantia,	@recebido,	@valor_conserto,	@pdf_nf_fornec,	@mudou_serie,	@config_garantia,	@qtde_dias_garantia,	@data_inicio_garantia,	@data_termino_garantia,	@id_ocorrencia,	@id_empresa)ENDGO------------------------------------------------------------