CREATE PROCEDURE spIns_Cg_tabela_servicos_fornecedor	@ID_FORNECEDOR As int,	@ID_TABELA As varchar(10),	@PRECO As numeric(12,2),	@ID_ITEM As uniqueidentifierASBEGININSERT INTO CG_TABELA_SERVICOS_FORNECEDOR (	id_fornecedor,	id_tabela,	preco,	id_item)VALUES (	@id_fornecedor,	@id_tabela,	@preco,	@id_item)END------------------------------------------------------------