CREATE PROCEDURE spUpd_Cg_tabela_servicos_fornecedor	@ID_FORNECEDOR As int,	@ID_TABELA As varchar(10),	@PRECO As numeric(12,2),	@ID_ITEM As uniqueidentifierASBEGINUPDATE CG_TABELA_SERVICOS_FORNECEDOR SET	id_fornecedor = @id_fornecedor,	id_tabela = @id_tabela,	preco = @precoWHERE	ID_ITEM = @ID_ITEMEND------------------------------------------------------------