CREATE PROCEDURE spIns_Cg_cliente	@ID_CLIENTE As int,	@SIGLA As varchar(50),	@NOME As varchar(50),	@CEP As varchar(9) = NULL,	@ENDERECO As varchar(70) = NULL,	@COMPLEMENTO As varchar(40) = NULL,	@CIDADE As varchar(50) = NULL,	@BAIRRO As varchar(50) = NULL,	@UF As char(2) = NULL,	@EMAIL As varchar(120) = NULL,	@TELEFONE1 As varchar(20) = NULL,	@CONTATO1 As varchar(50) = NULL,	@TELEFONE2 As varchar(20) = NULL,	@CONTATO2 As varchar(50) = NULL,	@TELEFONE3 As varchar(20) = NULL,	@CONTATO3 As varchar(50) = NULL,	@TELEFONE4 As varchar(20) = NULL,	@CONTATO4 As varchar(50) = NULL,	@OBS As varchar(250) = NULL,	@CADASTRO As datetime = NULL,	@ULTIMA_ATUALIZACAO As datetime = NULL,	@ID_EMPRESA As int,	@PFPJ INT =1, 
	@CPF_CNPJ VARCHAR(20),
	@RG_IE VARCHAR(25)ASBEGININSERT INTO CG_CLIENTE (	id_cliente,	sigla,	nome,	cep,	endereco,	complemento,	cidade,	bairro,	uf,	email,	telefone1,	contato1,	telefone2,	contato2,	telefone3,	contato3,	telefone4,	contato4,	obs,	cadastro,	ultima_atualizacao,	id_empresa,	PFPJ,	CPF_CNPJ,	RG_IE)VALUES (	@id_cliente,	@sigla,	@nome,	@cep,	@endereco,	@complemento,	@cidade,	@bairro,	@uf,	@email,	@telefone1,	@contato1,	@telefone2,	@contato2,	@telefone3,	@contato3,	@telefone4,	@contato4,	@obs,	@cadastro,	@ultima_atualizacao,	@id_empresa,	@PFPJ,	@CPF_CNPJ,	@RG_IE)END------------------------------------------------------------