CREATE PROCEDURE spUpd_Cg_cliente	@ID_CLIENTE As int,	@SIGLA As varchar(50),	@NOME As varchar(50),	@CEP As varchar(9) = NULL,	@ENDERECO As varchar(70) = NULL,	@COMPLEMENTO As varchar(40) = NULL,	@CIDADE As varchar(50) = NULL,	@BAIRRO As varchar(50) = NULL,	@UF As char(2) = NULL,	@EMAIL As varchar(120) = NULL,	@TELEFONE1 As varchar(20) = NULL,	@CONTATO1 As varchar(50) = NULL,	@TELEFONE2 As varchar(20) = NULL,	@CONTATO2 As varchar(50) = NULL,	@TELEFONE3 As varchar(20) = NULL,	@CONTATO3 As varchar(50) = NULL,	@TELEFONE4 As varchar(20) = NULL,	@CONTATO4 As varchar(50) = NULL,	@OBS As varchar(250) = NULL,	@CADASTRO As datetime = NULL,	@ULTIMA_ATUALIZACAO As datetime = NULL,	@ID_EMPRESA As int,	@PFPJ INT =1, 
	@CPF_CNPJ VARCHAR(20),
	@RG_IE VARCHAR(25)ASBEGINUPDATE CG_CLIENTE SET	sigla = @sigla,	nome = @nome,	cep = @cep,	endereco = @endereco,	complemento = @complemento,	cidade = @cidade,	bairro = @bairro,	uf = @uf,	email = @email,	telefone1 = @telefone1,	contato1 = @contato1,	telefone2 = @telefone2,	contato2 = @contato2,	telefone3 = @telefone3,	contato3 = @contato3,	telefone4 = @telefone4,	contato4 = @contato4,	obs = @obs,	cadastro = @cadastro,	ultima_atualizacao = @ultima_atualizacao,	id_empresa = @id_empresa,	PFPJ = @PFPJ,	CPF_CNPJ = @CPF_CNPJ,	RG_IE = @RG_IEWHERE	ID_CLIENTE = @ID_CLIENTEEND------------------------------------------------------------