CREATE PROCEDURE spIns_Cg_pedidovenda	@ID_PEDIDO As int,	@STATUS_PEDIDO As varchar(15),	@ID_CLIENTE As int,	@PREVISAO_ENTREGA As datetime,	@DATA_BAIXA As datetime = NULL,	@TOT_QTDE_ORIGINAL As int = NULL,	@TOT_QTDE_ENTREGAR As int = NULL,	@OBS AS TEXT = NULLASBEGININSERT INTO CG_PEDIDOVENDA (	id_pedido,	status_pedido,	id_cliente,	previsao_entrega,	data_baixa,	tot_qtde_original,	tot_qtde_entregar,	obs)VALUES (	@id_pedido,	@status_pedido,	@id_cliente,	@previsao_entrega,	@data_baixa,	@tot_qtde_original,	@tot_qtde_entregar,	@OBS)END------------------------------------------------------------