CREATE PROCEDURE spUpd_Cg_pedidovenda	@ID_PEDIDO As int,	@STATUS_PEDIDO As varchar(15),	@ID_CLIENTE As int,	@PREVISAO_ENTREGA As datetime,	@DATA_BAIXA As datetime = NULL,	@TOT_QTDE_ORIGINAL As int = NULL,	@TOT_QTDE_ENTREGAR As int = NULL,	@OBS AS TEXT = NULLASBEGINUPDATE CG_PEDIDOVENDA SET	status_pedido = @status_pedido,	id_cliente = @id_cliente,	previsao_entrega = @previsao_entrega,	data_baixa = @data_baixa,	tot_qtde_original = @tot_qtde_original,	tot_qtde_entregar = @tot_qtde_entregar,	obs = @OBSWHERE	ID_PEDIDO = @ID_PEDIDOEND------------------------------------------------------------