CREATE PROCEDURE spIns_Cg_cargo	@ID_CARGO As int,	@DESC_CARGO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_CARGO (	id_cargo,	desc_cargo,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_cargo,	@desc_cargo,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------