CREATE PROCEDURE spIns_Cg_entrada_chip	@ID_ROMANEIO As int,	@DATA_MOVTO As datetime,	@ID_LOJA As int,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_ENTRADA_CHIP (	id_romaneio,	data_movto,	id_loja,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_romaneio,	@data_movto,	@id_loja,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------