CREATE PROCEDURE spIns_Cg_modulo	@ID_MODULO As int,	@DESC_MODULO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_MODULO (	id_modulo,	desc_modulo,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_modulo,	@desc_modulo,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------