CREATE PROCEDURE spIns_Cg_motivo	@ID_MOTIVO As int,	@DESC_MOTIVO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_MOTIVO (	id_motivo,	desc_motivo,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_motivo,	@desc_motivo,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------