CREATE PROCEDURE spIns_Cg_status	@ID_STATUS As int,	@DESC_STATUS As varchar(20),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_STATUS (	id_status,	desc_status,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_status,	@desc_status,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------