CREATE PROCEDURE spIns_Cg_tipo_equipamento	@ID_TIPO_EQUIPAMENTO As int,	@DESC_TIPO_EQUIPAMENTO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_TIPO_EQUIPAMENTO (	id_tipo_equipamento,	desc_tipo_equipamento,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_tipo_equipamento,	@desc_tipo_equipamento,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------