CREATE PROCEDURE spIns_Cg_tipo_servico	@ID_TIPO_SERVICO As int,	@DESC_TIPO_SERVICO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGININSERT INTO CG_TIPO_SERVICO (	id_tipo_servico,	desc_tipo_servico,	user_ins,	data_ins,	user_upd,	data_upd)VALUES (	@id_tipo_servico,	@desc_tipo_servico,	@user_ins,	@data_ins,	@user_upd,	@data_upd)END------------------------------------------------------------