CREATE PROCEDURE spUpd_Cg_cargo	@ID_CARGO As int,	@DESC_CARGO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_CARGO SET	desc_cargo = @desc_cargo,	user_ins = user_ins,	data_ins = data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_CARGO = @ID_CARGOEND------------------------------------------------------------