create PROCEDURE spUpd_Cg_chip	@ID_CHIP As int,	@SIMID As varchar(20),	@ID_OPERADORA As int,	@ID_FORNECEDOR As int,	@CUSTO Numeric(12,2),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_CHIP SET	simid = @simid,	id_operadora = @id_operadora,	id_fornecedor = @id_fornecedor,	custo = @CUSTO,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_CHIP = @ID_CHIPEND------------------------------------------------------------