CREATE PROCEDURE spUpd_Cg_devolucao_equipamento_item	@ID_ROMANEIO As int,	@UNIQUE_KEYID As uniqueidentifier,	@ID_EQUIPAMENTO As int,	@SERIE As varchar(30),	@QTD As int,	@VALOR As numeric(12,2) = NULL,	@ID_MOTIVO As intASBEGINUPDATE CG_DEVOLUCAO_EQUIPAMENTO_ITEM SET	id_equipamento = @id_equipamento,	serie = @serie,	qtd = @qtd,	valor = @valor,	id_motivo = @id_motivoWHERE	ID_ROMANEIO = @ID_ROMANEIO AND	UNIQUE_KEYID = @UNIQUE_KEYIDEND------------------------------------------------------------