CREATE PROCEDURE spUpd_Cg_follow_up	@ID_OS As int,	@PROTOCOLO As varchar(20),	@DATA_HORA As datetime,	@TEXTO As text,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_FOLLOW_UP SET	data_hora = @data_hora,	texto = @texto,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_OS = @ID_OS AND	PROTOCOLO = @PROTOCOLOEND------------------------------------------------------------