CREATE PROCEDURE spUpd_Cg_modulo	@ID_MODULO As int,	@DESC_MODULO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_MODULO SET	desc_modulo = @desc_modulo,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_MODULO = @ID_MODULOEND------------------------------------------------------------