CREATE PROCEDURE spUpd_Cg_motivo	@ID_MOTIVO As int,	@DESC_MOTIVO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_MOTIVO SET	desc_motivo = @desc_motivo,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_MOTIVO = @ID_MOTIVOEND------------------------------------------------------------