CREATE PROCEDURE spUpd_Cg_movimento_chip_item	@ID_ROMANEIO As int,	@UNIQUE_KEYID As uniqueidentifier,	@ID_CHIP As int,	@SIMID As varchar(20),	@QTD As int,	@VALOR As numeric(12,2) = NULLASBEGINUPDATE CG_MOVIMENTO_CHIP_ITEM SET	id_chip = @id_chip,	simid = @simid,	qtd = @qtd,	valor = @valorWHERE	ID_ROMANEIO = @ID_ROMANEIO AND	UNIQUE_KEYID = @UNIQUE_KEYIDEND------------------------------------------------------------