CREATE PROCEDURE spUpd_Cg_responsavel	@ID_RESPONSAVEL As int,	@NOME As varchar(50),	@APELIDO As varchar(50) = NULL,	@EMAIL As varchar(120) = NULL,	@CELULAR As varchar(20) = NULL,	@WHATSAPP As varchar(20) = NULL,	@ID_CARGO As int,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_RESPONSAVEL SET	nome = @nome,	apelido = @apelido,	email = @email,	celular = @celular,	whatsapp = @whatsapp,	id_cargo = @id_cargo,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_RESPONSAVEL = @ID_RESPONSAVELEND------------------------------------------------------------