CREATE PROCEDURE spUpd_Cg_sequencial	@TABELA As varchar(40),	@KEYID As int,	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_SEQUENCIAL SET	keyid = @keyid,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	TABELA = @TABELAEND------------------------------------------------------------