CREATE PROCEDURE spUpd_Cg_status	@ID_STATUS As int,	@DESC_STATUS As varchar(20),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_STATUS SET	desc_status = @desc_status,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_STATUS = @ID_STATUSEND------------------------------------------------------------