CREATE PROCEDURE spUpd_Cg_tipo_equipamento	@ID_TIPO_EQUIPAMENTO As int,	@DESC_TIPO_EQUIPAMENTO As varchar(50),	@USER_INS As int = NULL,	@DATA_INS As datetime = NULL,	@USER_UPD As int = NULL,	@DATA_UPD As datetime = NULLASBEGINUPDATE CG_TIPO_EQUIPAMENTO SET	desc_tipo_equipamento = @desc_tipo_equipamento,	user_ins = @user_ins,	data_ins = @data_ins,	user_upd = @user_upd,	data_upd = @data_updWHERE	ID_TIPO_EQUIPAMENTO = @ID_TIPO_EQUIPAMENTOEND------------------------------------------------------------